import React from "react";
import { Divider } from "antd";
import { GoogleLogin, GoogleOAuthProvider } from "@react-oauth/google";
import { FacebookProvider, LoginButton } from "react-facebook";
import { getReq } from "../../services/reqClient";
import TextField from "@mui/material/TextField";
export default function Login() {
  const client_id =
    "86706621088-sp32jf15h3pon51k4tilvophag17tba2.apps.googleusercontent.com";
  // const handleLoginWithGoogle = async (credentialResponse: any) => {
  //   console.log("check credential: ", credentialResponse);
  //   window.open("http://localhost:3000/auth/google");
  //   const res = await getReq("http://localhost:3000/auth/google");
  // };
  const handleLogin = async () => {};

  const handleLoginWithGoogle = (response: any) => {
    console.log("Login Success:", response);
  };
  return (
    <section className="bg-gray-50 dark:bg-gray-900 h-full w-full">
      <div className="flex flex-col items-center justify-center px-6 py-8 mx-auto md:h-screen lg:py-0">
        <div className="w-full bg-white rounded-lg shadow dark:border md:mt-0 sm:max-w-md xl:p-0 dark:bg-gray-800 dark:border-gray-700">
          <div className="p-6 space-y-4 md:space-y-6 sm:p-8">
            <h1 className="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl dark:text-white">
              Sign in to your account
            </h1>
            <form className="space-y-4 md:space-y-6" action="#">
              <TextField
                id="outlined-password-input"
                label="Email"
                type="email"
                autoComplete="current-email"
                className="w-full"
                required
              />
              <TextField
                id="outlined-password-input"
                label="Password"
                type="password"
                autoComplete="current-password"
                className="w-full"
                required
              />

              <button
                type="submit"
                className="w-full text-white bg-primary-600 hover:bg-primary-700 focus:ring-4 focus:outline-none focus:ring-primary-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-primary-600 dark:hover:bg-primary-700 dark:focus:ring-primary-800"
                onClick={handleLogin}
              >
                Sign in
              </button>
              <Divider>Or Sign In with</Divider>
              <GoogleOAuthProvider clientId={client_id}>
                <GoogleLogin
                  onSuccess={handleLoginWithGoogle}
                  onError={() => {
                    console.log("Login Failed");
                  }}
                />
              </GoogleOAuthProvider>
              <FacebookProvider appId="123456789">
                <LoginButton
                  scope="email"
                  onError={handleError}
                  onSuccess={handleSuccess}
                >
                  Login via Facebook
                </LoginButton>
              </FacebookProvider>
              <p className="text-sm font-light text-gray-500 dark:text-gray-400">
                Don’t have an account yet?{" "}
                <a
                  href="#"
                  className="font-medium text-primary-600 hover:underline dark:text-primary-500"
                >
                  Sign up
                </a>
              </p>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
